import React, { useEffect, useState } from "react";
import Search from "./Search";
import "./db.json";

function Main() {
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("https://kevinkkimutai.github.io/charity/db.json")
      .then((res) => res.json())
      .then((data) => setData(data));
  }, []);
    console.log(data.project);


  const filterProjects = data.filter(
      (projects) =>
          projects.description &&
          projects.description.toLowerCase().includes(search.toLowerCase())
  );
    const transactionList = filterProjects.map((project) => {
        return (
            <li key={project.id}>
                <div className="card">{project.name}</div>
                <div className="card">{project.description}</div>
            </li>
        )
    })
  return (
    <div>
          <Search search={search} setSearch={setSearch} />
          {transactionList}
      <div>


      </div>
    </div>
  );
}

export default Main;
